function outputFolder(folder, spaces) {
  const data = [];

  data.push(`${" ".repeat(spaces)}- ${folder.label}`);

  for (const child of folder.children) {
    data.push(...outputFolder(child, spaces + 2));
  }

  for (const entry of folder.entries) {
    data.push(`${" ".repeat(spaces + 2)}- ${entry.label}`);
  }

  return data;
}

function outputRootFolder(rootFolder) {
  const data = [];

  data.push(`<details>`);
  data.push(`  <summary>Available Automations</summary>`);
  data.push(``);

  for (const child of rootFolder.children) {
    data.push(...outputFolder(child, 2));
  }

  for (const entry of rootFolder.entries) {
    data.push(`  - ${entry.label}`);
  }

  data.push(``);
  data.push(`</details>`);

  return data;
}

await navigator.clipboard.writeText(outputRootFolder(game.assistant.storage.getRootFolder()).join("\n"));
